// Configuración del input
// Si viene del webhook, usar esos datos
// Si es manual, usar datos de prueba

let input = $input.first()?.json || {};

// Datos por defecto para pruebas manuales
const tenant_id = input.tenant_id || '00000000-0000-0000-0000-000000000000';
const user_id = input.user_id || '00000000-0000-0000-0000-000000000001';
const message = input.message || '¿Cómo va mi negocio esta semana?';

const today = new Date().toISOString().split('T')[0];

return [{ 
  json: { 
    tenant_id, 
    user_id, 
    message,
    today 
  } 
}];

// KPI Calculator - Calcula métricas del negocio

const inputData = $('Normalize Input').first().json;
const tenant_id = inputData.tenant_id;
const user_id = inputData.user_id;
const message = inputData.message;
const today = inputData.today;
const todayDate = new Date(today);

// Obtener datos
const properties = $('Get Properties').all().map(item => item.json);
const bookings = $('Get Bookings').all().map(item => item.json);
const alerts = $('Get Open Alerts').all().map(item => item.json);

// Fechas de referencia
const last30Days = new Date(todayDate);
last30Days.setDate(last30Days.getDate() - 30);
const last30DaysStr = last30Days.toISOString().split('T')[0];

const next7Days = new Date(todayDate);
next7Days.setDate(next7Days.getDate() + 7);
const next7DaysStr = next7Days.toISOString().split('T')[0];

const next30Days = new Date(todayDate);
next30Days.setDate(next30Days.getDate() + 30);
const next30DaysStr = next30Days.toISOString().split('T')[0];

// ========================================
// CALCULAR KPIs
// ========================================

// Bookings últimos 30 días
const bookingsLast30Days = bookings.filter(b => 
  b.check_in >= last30DaysStr && 
  b.check_in <= today && 
  b.status === 'confirmed'
);

// Revenue últimos 30 días
const revenueLast30Days = bookingsLast30Days.reduce((sum, b) => {
  return sum + (parseFloat(b.total_price) || 0);
}, 0);

// Noches reservadas últimos 30 días
const nightsBookedLast30Days = bookingsLast30Days.reduce((sum, b) => {
  const checkIn = new Date(b.check_in);
  const checkOut = new Date(b.check_out);
  const nights = Math.ceil((checkOut - checkIn) / (1000 * 60 * 60 * 24));
  return sum + (nights > 0 ? nights : 0);
}, 0);

// Ocupación promedio (aproximada)
const totalNightsAvailable = properties.length * 30;
const occupancyAvg = totalNightsAvailable > 0 
  ? Math.round((nightsBookedLast30Days / totalNightsAvailable) * 100) 
  : 0;

// ADR (Average Daily Rate)
const adr = nightsBookedLast30Days > 0 
  ? Math.round(revenueLast30Days / nightsBookedLast30Days) 
  : 0;

// RevPAR
const revpar = Math.round(adr * (occupancyAvg / 100));

// Check-ins hoy
const checkinsToday = bookings.filter(b => b.check_in === today && b.status === 'confirmed');

// Check-outs hoy
const checkoutsToday = bookings.filter(b => b.check_out === today && b.status === 'confirmed');

// Huéspedes in-house
const inHouse = bookings.filter(b => 
  b.check_in <= today && 
  b.check_out > today && 
  b.status === 'confirmed'
);

// Bookings próximos 7 días
const bookingsNext7Days = bookings.filter(b => 
  b.check_in >= today && 
  b.check_in <= next7DaysStr && 
  b.status === 'confirmed'
);

// Bookings próximos 30 días
const bookingsNext30Days = bookings.filter(b => 
  b.check_in >= today && 
  b.check_in <= next30DaysStr && 
  b.status === 'confirmed'
);

// Revenue próximos 7 días
const revenueNext7Days = bookingsNext7Days.reduce((sum, b) => {
  return sum + (parseFloat(b.total_price) || 0);
}, 0);

// Propiedades sin reservas próximos 14 días
const next14Days = new Date(todayDate);
next14Days.setDate(next14Days.getDate() + 14);
const next14DaysStr = next14Days.toISOString().split('T')[0];

const bookingsNext14Days = bookings.filter(b => 
  b.check_in >= today && 
  b.check_in <= next14DaysStr && 
  b.status === 'confirmed'
);
const propertyIdsWithBookings = [...new Set(bookingsNext14Days.map(b => b.property_id))];
const propertiesWithoutBookings = properties.filter(p => !propertyIdsWithBookings.includes(p.id));

// ========================================
// RESUMEN DE PROPIEDADES
// ========================================
const propertySummary = properties.slice(0, 5).map(prop => {
  const propBookings = bookingsLast30Days.filter(b => b.property_id === prop.id);
  const propRevenue = propBookings.reduce((sum, b) => sum + (parseFloat(b.total_price) || 0), 0);
  const propNights = propBookings.reduce((sum, b) => {
    const nights = Math.ceil((new Date(b.check_out) - new Date(b.check_in)) / (1000 * 60 * 60 * 24));
    return sum + (nights > 0 ? nights : 0);
  }, 0);

  return {
    id: prop.id,
    name: prop.name || 'Sin nombre',
    revenue_30d: propRevenue,
    nights_30d: propNights,
    occupancy_30d: Math.round((propNights / 30) * 100)
  };
});

// ========================================
// OUTPUT
// ========================================
const kpis = {
  // Últimos 30 días
  revenue_last_30d: revenueLast30Days,
  nights_booked_30d: nightsBookedLast30Days,
  occupancy_avg: occupancyAvg,
  adr: adr,
  revpar: revpar,

  // Hoy
  checkins_today: checkinsToday.length,
  checkouts_today: checkoutsToday.length,
  in_house: inHouse.length,

  // Próximos días
  bookings_next_7d: bookingsNext7Days.length,
  bookings_next_30d: bookingsNext30Days.length,
  revenue_next_7d: revenueNext7Days,

  // Propiedades
  total_properties: properties.length,
  properties_without_bookings_14d: propertiesWithoutBookings.length,

  // Alertas
  open_alerts: alerts.length
};

const context = {
  tenant_id,
  user_id,
  message,
  today,
  kpis,
  properties_summary: propertySummary,
  open_alerts: alerts.slice(0, 10).map(a => ({
    type: a.alert_type,
    title: a.title,
    severity: a.severity
  })),
  properties_without_bookings: propertiesWithoutBookings.slice(0, 5).map(p => p.name || p.id)
};

return [{ json: context }];

