parameters.jsCode:
// Build Master Event v1.0 for WF-SP-01
const webhook = $('Webhook').item.json;
const extractText = $('Extract Text').item.json;

const messageText = (extractText.text || '').replace(/\n/g, ' ').replace(/"/g, '');

// Generate UUID without crypto
function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

return [{
  json: {
    schema_version: "1.0",
    event_id: generateUUID(),
    event_type: "lead_inbound",
    source: "whatsapp",
    timestamp: new Date().toISOString(),

    tenant: {
      tenant_id: "c24393db-d318-4d75-8bbf-0fa240b9c1db",
      property_id: "18711359-1378-4d12-9ea6-fb31c0b1bac2"
    },

    contact: {
      name: webhook.body.entry[0].changes[0].value.contacts?.[0]?.profile?.name || null,
      phone: webhook.body.entry[0].changes[0].value.messages[0].from,
      email: null,
      language: "en"
    },

    message: {
      channel: "whatsapp",
      message_id: webhook.body.entry[0].changes[0].value.messages[0].id,
      text: messageText,
      raw: webhook.body
    },

    context: {
      intent: null,
      checkin: null,
      checkout: null,
      guests: null,
      budget: null,
      property_hint: null
    },

    meta: {
      flow_origin: "WF-WA-BANYU",
      trace_id: generateUUID()
    }
  }
}];
