// Extract callResult from VAPI Structured Output
// VAPI sends multiple events - only process end-of-call-report (has callResult)
const body = $input.first().json.body || $input.first().json;

const structuredOutputId = '6426dbc9-8b9e-49f7-8f29-faa16683bcda';

let callResult = null;
let callerNumber = null;
let callId = null;

// Method 1: Direct UUID key at root
if (body[structuredOutputId]?.name === 'callResult') {
  callResult = body[structuredOutputId].result;
}

// Method 2: Scan all root keys for any structured output
if (!callResult) {
  for (const key of Object.keys(body)) {
    if (body[key]?.name === 'callResult' && body[key]?.result) {
      callResult = body[key].result;
      break;
    }
  }
}

// Method 3: Fallback to message.artifact path
if (!callResult) {
  const artifact = body.message?.artifact || body.artifact || {};
  const outputs = artifact.structuredOutputs || {};
  for (const key of Object.keys(outputs)) {
    if (outputs[key]?.name === 'callResult') {
      callResult = outputs[key].result;
      break;
    }
  }
}

// If no callResult, this is not an end-of-call-report - return empty to stop workflow silently
if (!callResult) {
  return [];
}

// Get caller info
const call = body.message?.call || body.call || {};
callId = call.id || null;
callerNumber = call.customer?.number || callResult.contact?.phone || null;

return [{
  json: {
    callResult: callResult,
    callId: callId,
    callerNumber: callerNumber
  }
}];

