// Configuración del tenant (MVP: tenant fijo)
const tenant_id = '00000000-0000-0000-0000-000000000000';
const today = new Date().toISOString().split('T')[0];

return [{ json: { tenant_id, today } }];

// Rule Engine - Evalúa reglas de negocio y genera alertas

const tenantConfig = $('Set Tenant Config').first().json;
const tenant_id = tenantConfig.tenant_id;
const today = tenantConfig.today;
const todayDate = new Date(today);

// Obtener datos
const properties = $('Get Properties').all().map(item => item.json);
const bookings = $('Get Bookings').all().map(item => item.json);

// Fechas de referencia
const next7Days = new Date(todayDate);
next7Days.setDate(next7Days.getDate() + 7);
const next7DaysStr = next7Days.toISOString().split('T')[0];

const next14Days = new Date(todayDate);
next14Days.setDate(next14Days.getDate() + 14);
const next14DaysStr = next14Days.toISOString().split('T')[0];

// Array para alertas
const alerts = [];

// ========================================
// REGLA 1: Ocupación < 40% próximos 7 días
// ========================================
const bookingsNext7Days = bookings.filter(b => 
  b.check_in >= today && 
  b.check_in <= next7DaysStr && 
  b.status === 'confirmed'
);

const totalProperties = properties.length || 1;
const occupancyNext7Days = (bookingsNext7Days.length / (totalProperties * 7)) * 100;

if (occupancyNext7Days < 40) {
  alerts.push({
    tenant_id,
    alert_type: 'low_occupancy',
    severity: 'high',
    title: 'Ocupación baja próximos 7 días',
    description: `Ocupación actual: ${Math.round(occupancyNext7Days)}%. Solo ${bookingsNext7Days.length} reservas para ${totalProperties} propiedades.`,
    status: 'open',
    property_id: null
  });
}

// ========================================
// REGLA 2: Propiedad sin reservas 14 días
// ========================================
const bookingsNext14Days = bookings.filter(b => 
  b.check_in >= today && 
  b.check_in <= next14DaysStr && 
  b.status === 'confirmed'
);

const propertyIdsWithBookings = [...new Set(bookingsNext14Days.map(b => b.property_id))];

properties.forEach(prop => {
  if (!propertyIdsWithBookings.includes(prop.id)) {
    alerts.push({
      tenant_id,
      alert_type: 'no_bookings',
      severity: 'medium',
      title: `Sin reservas: ${prop.name || prop.id}`,
      description: `La propiedad no tiene reservas en los próximos 14 días.`,
      status: 'open',
      property_id: prop.id
    });
  }
});

// ========================================
// REGLA 3: Muchas cancelaciones recientes
// ========================================
const last7Days = new Date(todayDate);
last7Days.setDate(last7Days.getDate() - 7);
const last7DaysStr = last7Days.toISOString().split('T')[0];

const recentCancellations = bookings.filter(b => 
  b.status === 'cancelled' && 
  b.updated_at && 
  b.updated_at.split('T')[0] >= last7DaysStr
);

if (recentCancellations.length >= 3) {
  alerts.push({
    tenant_id,
    alert_type: 'high_cancellations',
    severity: 'high',
    title: 'Alto número de cancelaciones',
    description: `${recentCancellations.length} cancelaciones en los últimos 7 días. Revisar política de cancelación.`,
    status: 'open',
    property_id: null
  });
}

// ========================================
// REGLA 4: Check-ins hoy sin confirmar
// ========================================
const checkinsToday = bookings.filter(b => 
  b.check_in === today && 
  b.status !== 'confirmed' && 
  b.status !== 'cancelled'
);

if (checkinsToday.length > 0) {
  alerts.push({
    tenant_id,
    alert_type: 'pending_checkins',
    severity: 'high',
    title: 'Check-ins pendientes de confirmar',
    description: `${checkinsToday.length} check-in(s) hoy sin estado confirmado.`,
    status: 'open',
    property_id: null
  });
}

// ========================================
// OUTPUT
// ========================================
const output = {
  tenant_id,
  date: today,
  rules_evaluated: 4,
  alerts_generated: alerts.length,
  alerts,
  summary: {
    total_properties: totalProperties,
    bookings_next_7d: bookingsNext7Days.length,
    occupancy_next_7d: Math.round(occupancyNext7Days),
    cancellations_last_7d: recentCancellations.length,
    checkins_today_pending: checkinsToday.length
  }
};

return [{ json: output }];

// Preparar alertas para inserción individual
const input = $('Rule Engine').first().json;
const alerts = input.alerts || [];

if (alerts.length === 0) {
  return [{ json: { skip: true } }];
}

// Retornar cada alerta como item separado
return alerts.map(alert => ({ json: alert }));
